import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

// ✅ Only include endpoints that are implemented
const chartConfigs = [
  { label: "Salary by Country", endpoint: "region" },
  { label: "Remote vs On-site", endpoint: "workmode" },
  { label: "Salary by Education", endpoint: "education" },
  // Uncomment these when implemented:
  // { label: "Gender Gap Analysis", endpoint: "gender" },
  // { label: "Salary by Tech Stack", endpoint: "tech" },
];

const SalaryCharts = () => {
  const [chartsData, setChartsData] = useState({});

  useEffect(() => {
    chartConfigs.forEach(async (config) => {
      try {
        const res = await axios.get(`http://localhost:5000/api/trends/${config.endpoint}`);
        setChartsData((prev) => ({
          ...prev,
          [config.endpoint]: res.data.data,
        }));
      } catch (err) {
        console.error(`❌ Failed to fetch ${config.endpoint} data`, err.message);
      }
    });
  }, []);

  const renderChart = (endpoint, label) => {
    const data = chartsData[endpoint];
    if (!data || data.length === 0) return null;

    return (
      <div key={endpoint} className="mb-10">
        <h3 className="text-xl font-semibold mb-2">{label}</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="label" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="avgSalary" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Salary Insights</h2>
      {chartConfigs.map((config) => renderChart(config.endpoint, config.label))}
    </div>
  );
};

export default SalaryCharts;
