import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import { SignIn, SignUp, SignedIn, SignedOut, UserButton, RedirectToSignIn, useUser } from "@clerk/clerk-react";

import DeveloperDashboard from "./pages/DeveloperDashboard";
import RecruiterDashboard from "./pages/RecruiterDashboard";
import AdminDashboard from "./pages/AdminDashboard";

const Home = () => {
  const { user } = useUser();
  const role = user?.publicMetadata?.role;

  if (!role) return <p>No role assigned. Contact admin.</p>;

  if (role === "developer") return <Navigate to="/developer" />;
  if (role === "recruiter") return <Navigate to="/recruiter" />;
  if (role === "admin") return <Navigate to="/admin" />;

  return <p>Invalid role. Contact admin.</p>;
};

function App() {
  return (
    <>
      <Routes>
        <Route path="/sign-in" element={<SignIn routing="path" path="/sign-in" />} />
        <Route path="/sign-up" element={<SignUp routing="path" path="/sign-up" />} />

        <Route path="/" element={
          <>
            <SignedIn>
              <Home />
            </SignedIn>
            <SignedOut>
              <RedirectToSignIn />
            </SignedOut>
          </>
        } />

        <Route path="/developer" element={<DeveloperDashboard />} />
        <Route path="/recruiter" element={<RecruiterDashboard />} />
        <Route path="/admin" element={<AdminDashboard />} />

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </>
  );
}

export default App;
