// client/src/components/developer/SalaryCharts.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid,
} from "recharts";

const chartConfigs = [
  { label: "Salary by Country", endpoint: "country", key: "Country" },
  { label: "Salary by Tech Stack", endpoint: "tech", key: "TechStack" },
  { label: "Remote vs On-site", endpoint: "remote", key: "WorkLocation" },
  { label: "Salary by Education", endpoint: "education", key: "Education" },
  { label: "Gender Gap Analysis", endpoint: "gender", key: "Gender" },
];

const SalaryCharts = () => {
  const [chartsData, setChartsData] = useState({});

  useEffect(() => {
    chartConfigs.forEach(async (config) => {
      try {
        const res = await axios.get(`http://localhost:5000/api/trends/${config.endpoint}`);
        setChartsData((prev) => ({ ...prev, [config.key]: res.data.data }));
      } catch (err) {
        console.error(`Failed to fetch ${config.endpoint} data`, err);
      }
    });
  }, []);

  const renderChart = (key, label) => {
    const data = chartsData[key];
    if (!data) return null;

    return (
      <div key={key} className="mb-10">
        <h3 className="text-xl font-semibold mb-2">{label}</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey={key} />
            <YAxis />
            <Tooltip />
            <Bar dataKey="avgSalary" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Salary Insights</h2>
      {chartConfigs.map((config) => renderChart(config.key, config.label))}
    </div>
  );
};

export default SalaryCharts;
