import React, { useEffect, useState } from "react";
import axios from "axios";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from "recharts";

const ExperienceTrendsChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchTrends = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/experience-trends");
        if (res.data.success) setData(res.data.data);
      } catch (err) {
        console.error("Failed to fetch experience trends:", err);
      }
    };

    fetchTrends();
  }, []);

  return (
    <div style={{ marginTop: "40px" }}>
      <h3>ðŸ“ˆ Salary vs Experience</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data}>
          <XAxis dataKey="experience" label={{ value: "YearsCodePro", position: "insideBottomRight", offset: -5 }} />
          <YAxis />
          <CartesianGrid strokeDasharray="3 3" />
          <Tooltip />
          <Line type="monotone" dataKey="avgSalary" stroke="#8884d8" strokeWidth={2} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ExperienceTrendsChart;
